빈도수

코드penguins |&gt;
  drop_na() |&gt; 
  count(species, sex) |&gt; 
  ggplot(aes(x = fct_reorder(species, -n), y = n, fill = sex)) +
    geom_col(width=0.5) +
    labs(x = "펭귄 종", y = "펭귄 빈도수",
         fill = "암수",
         title = "남극 서식 펭귄종 통계 빈도 분석") +
    theme_minimal(base_family = "AppleGothic")

